import React from 'react';
import { range } from '../../utils';
import { checkGuess } from '../../game-helpers';

function Guess({ value, answer }) {
// console.log(value.answer)
  //VALUE -> {}
  //checkedValue -> [{},{}]

    //
  let checkedValue;
    if(value){
      checkedValue = checkGuess(value.answer, answer);
      console.log('value: ' + value.answer);
    }




  // console.log('checked value: '+checkedValue);
  // const importantClass = checkedValue ? `cell ${checkedValue.status}` : 'cell';
  return (
    <p className='guess'>
      {range(5).map((cell) => (
        <span
          className={checkedValue ? `cell ${checkedValue[cell].status}`:'cell'}
          key={value ? `${value.id}-${cell}` : cell}
        >
          {value ? checkedValue[cell].letter : undefined}
        </span>
      ))}
    </p>
  );
}

export default Guess;
