import React from 'react';
import { NUM_OF_GUESSES_ALLOWED } from '../../constants';
import { range } from '../../utils';

function Guess({ guesses }) {
  // const [numOfGuess, setNumOfGuess] = React.useState(range(NUM_OF_GUESSES_ALLOWED));

  return (
    <div className="guess-results">
      {range(6).map((row,index)=>(
        <p className="guess" key={index}>
          <span className="cell">{guesses[row] && guesses[0].answer[0]}</span>
          {/*<span className="cell">{guesses[0] && guesses[0].answer[1]}</span>*/}
          {/*<span className="cell">{guesses[0] && guesses[0].answer[2]}</span>*/}
          {/*<span className="cell">{guesses[0] && guesses[0].answer[3]}</span>*/}
          {/*<span className="cell">{guesses[0] && guesses[0].answer[4]}</span>*/}
        </p>
      ))}
    </div>
  );
}

export default Guess;
